import React, { useState, useEffect } from "react";

import People from '../../assets/people';

const PersonInfo = (props) => {

  const [user, setUser] = useState(null);

  useEffect(() => {
    if (props.modalIsOpen) {
      let parsedScan = props.scan.toLowerCase();
      parsedScan = parsedScan.includes(/\s/g) ? parsedScan.replace(' ', '-') : parsedScan;

      const text = parsedScan;

      scanToPerson(text);
    }

  });

  const scanToPerson = (text) => {

    console.log(parsedScan)

    for (let i = 0; i < People.length; i++) {
      let nameToMatch = People[i].name.replace(/\s/g, '-').toLowerCase();
      if (parsedScan == nameToMatch) {
        setUser(People[i]);
      }
    }
  }

  return (
    <div>

      {
        user ?
          <>
            <h1>{user.name}</h1>
            <p>{user.bio}</p>
          </>
        :
          ''
      }

    </div>
  )
}

export default PersonInfo
