import React, { useState, useEffect } from "react";

import People from '../../assets/people';

const PersonInfo = (props) => {

  const parsedScan = props.scan.replace(/\s/g, '-').toLowerCase

  const scanToPerson = () => {

    for (let i = 0; i < People.length; i++) {
      if (parsedScan == People[i].name) {
        return People[i];
      }
    }

  }

  return (
    <div>
      <h1>{scanToPerson(props.scan).name}</h1>
    </div>
  )
}

export default PersonInfo
