import React, { useState } from "react";

import {Html5QrcodeScanner} from 'html5-qrcode';

const Scanner = () => {

  state = {
    result: 'No result'
  }

  handleScan = data => {
    if (data) {
      this.setState({
        result: data
      })
    }
  }
  handleError = err => {
    console.error(err)
  }

  return (
    <div>
      <Html5QrcodeScannerPlugin
            fps={10}
            qrbox={250}
            disableFlip={false}
            qrCodeSuccessCallback={console.log}
            qrCodeErrorCallback={console.error} />

      <div id="scan-button">
          <h2>Scan</h2>
      </div>

    </div>
  )
}

export default Scanner;
