import React, { useState } from "react";

// this will be all QR code shit
const scan = async (html5QrCode) => {
  const qrCodeSuccessCallback = (decodedText, decodedResult) => {
    console.log(decodedResult, decodedText);

    const predictionTitle = document.getElementById("prediction-title");

    predictionTitle.innerHTML = decodedText;
  };
  const config = { fps: 10, qrbox: { width: 200, height: 200 } };

  // Select back camera or fail with `OverconstrainedError`.
  html5QrCode.start({ facingMode: { exact: "environment"} }, config, qrCodeSuccessCallback);
}

const Scanner = () => {

  const html5QrCode = new Html5Qrcode("videoStream");

  const beginScan = () => {
    console.log('scan beginning');
    scan(html5QrCode);
  }
  // set video variable

  return (
    <div>
      <div id='videoStream' width="90%"></div>

      <div id="scan-button" onClick={beginScan}>
          <h2>Scan</h2>
      </div>

    </div>
  )
}

export default Scanner;
