import React, { useState } from "react";

import QrReader from 'react-qr-reader'

const Scanner = () => {

  const [isScanned, setScan] = useState(null);

  const handleScan = data => {
    if (data) {
      setScan(data);
      console.log(data);
    }
  }
  const handleError = err => {
    console.error(err)
  }

  return (
    <div>
      <div>
        <QrReader
          delay={300}
          onError={handleError}
          onScan={handleScan}
          style={{ width: '50%' }}
        />
        <p></p>
      </div>

      <div id="scan-button">
          <h2>Scan</h2>
      </div>

    </div>
  )
}

export default Scanner;
