import React, { useState } from "react";

import QrReader from 'react-qr-reader'

const Scanner = () => {

  state = {
    result: 'No result'
  }

  const handleScan = data => {
    if (data) {
      this.setState({
        result: data
      })
    }
  }
  const handleError = err => {
    console.error(err)
  }

  return (
    <div>
      <div>
        <QrReader
          delay={300}
          onError={handleError}
          onScan={handleScan}
          style={{ width: '100%' }}
        />
        <p>{this.state.result}</p>
      </div>

      <div id="scan-button">
          <h2>Scan</h2>
      </div>

    </div>
  )
}

export default Scanner;
