import QrReader from 'react-qr-reader'

const Scanner = () => {

  const [isScanned, setScan] = useState(null);

  const handleScan = data => {
    console.log('scan?')
    // if (data) {
    //   setScan(data);
    //   console.log(data);
    // }
  }
  const handleError = err => {
    console.error(err)
  }

  return (
    <div id="scanner">
      <QrReader
        id="reader"
        delay={300}
        onError={handleError}
        onScan={handleScan}
        facingMode={"environment"}
        style={{ width: '80%' }}
      />

      <div id="scan-button">
          <h2>Scan</h2>
      </div>

    </div>
  )
}

export default Scanner;
