import React, { useState } from "react";

import {Html5QrcodeScanner} from 'html5-qrcode';

class Html5QrcodeScannerPlugin extends React.Component {
  componentDidMount() {
      // Creates the configuration object for Html5QrcodeScanner.
      function createConfig(props) {
          var config = {};
          if (props.fps) {
          config.fps = props.fps;
          }
          if (props.qrbox) {
          config.qrbox = props.qrbox;
          }
          if (props.aspectRatio) {
          config.aspectRatio = props.aspectRatio;
          }
          if (props.disableFlip !== undefined) {
          config.disableFlip = props.disableFlip;
          }
          return config;
      }

      var config = createConfig(this.props);
      var verbose = this.props.verbose === true;

      // Suceess callback is required.
      if (!(this.props.qrCodeSuccessCallback )) {
          throw 'qrCodeSuccessCallback is required callback.';
      }

      this.html5QrcodeScanner = new Html5QrcodeScanner(
          'qr-code-full-region', config, verbose);
      this.html5QrcodeScanner.render(
          this.props.qrCodeSuccessCallback, this.props.qrCodeErrorCallback);
  }

  componentWillUnmount() {
      // TODO(mebjas): See if there is a better way to handle
      //  promise in `componentWillUnmount`.
      this.html5QrcodeScanner.clear().catch(error => {
          console.error('Failed to clear html5QrcodeScanner. ', error);
      });
  }

  render() {
      return <div id={'qr-code-full-region'} />;
  }
}

const Scanner = () => {

  //const html5QrCode = new Html5Qrcode("videoStream");

  // const beginScan = () => {
  //   console.log('scan beginning');
  //   scan(html5QrCode);
  // }
  // set video variable

  return (
    <div>
      <Html5QrcodeScannerPlugin
            fps={10}
            qrbox={250}
            disableFlip={false}
            qrCodeSuccessCallback={console.log}
            qrCodeErrorCallback={console.error} />

      <div id="scan-button">
          <h2>Scan</h2>
      </div>

    </div>
  )
}

export default Scanner;
