import React, { useState, useEffect } from "react";

import People from '../../assets/people';

const PersonInfo = (props) => {

  const parsedScan = props.scan.replace(/\s/g, '-');

  const scanToPerson = () => {

    for (let i = 0; i < People.length; i++) {
      if (parsedScan == People[i].name.toLowerCase()) {
        return People[i];
      }
    }

  }

  let person = scanToPerson();

  return (
    <div>
      <h1>{person.name}</h1>
    </div>
  )
}

export default PersonInfo
