import React, { useState } from "react";

import QrReader from 'react-qr-reader';

import FocusedPerson from '../FocusedPerson/FocusedPerson'

const Scanner = () => {

  const [isScanned, setScan] = useState(null);

  const handleScan = data => {
    if (data) {
      setScan(data);
      console.log(data);
    }
  }
  const handleError = err => {
    console.error(err)
  }

  return (
    <div id="scanner">
      <QrReader
        id="reader"
        delay={300}
        onError={handleError}
        onScan={handleScan}
        facingMode={"environment"}
        style={{ width: '100%' }}
      />

    </div>
  )
}

export default Scanner;
