import React, { useState } from "react";

import QrReader from 'react-qr-reader'

const Scanner = () => {

  const [isScanned, setActive] = useState(false);

  const handleToggle = () => {
    setActive(!isActive);
  };

  const handleScan = data => {
    if (data) {
      this.setState({
        result: data
      })
    }
  }
  const handleError = err => {
    console.error(err)
  }

  return (
    <div>
      <div>
        <QrReader
          delay={300}
          onError={handleError}
          onScan={handleScan}
          style={{ width: '50%' }}
        />
        <p></p>
      </div>

      <div id="scan-button">
          <h2>Scan</h2>
      </div>

    </div>
  )
}

export default Scanner;
