import React, { useState } from "react";

import QrReader from 'react-qr-reader';

// BasicModal Component
import Modal, { useModalState } from "react-simple-modal-provider";

const Scanner = () => {

  const [isScanned, setScan] = useState(false);

  const [focusedPerson, setFocusedPerson] = useState(null);

  const [isOpen, setOpen] = useModalState();

  const handleScan = data => {
    if (data) {
      setScan(true);
      setFocusedPerson(data);
      console.log(data);
    }
  }

  const handleError = err => {
    console.error(err)
  }

  // close modal
  const clearScan = () => {
    if (isScanned) {
      setScan(false);
      setFocusedPerson(null);
    }
  }

  return (
    <div id="scanner">
      <QrReader
        id="reader"
        delay={300}
        onError={handleError}
        onScan={handleScan}
        facingMode={"environment"}
        style={{ width: '100%' }}
      />

      <Modal
        id={"BasicModal"}
        isOpen={isScanned}
        setOpen={setOpen}
      >
        <span>ðŸ˜†</span>
      </Modal>

    </div>
  )
}

export default Scanner;
